\n                                      <td valign=\"top\"><p align=\"center\">6.</p></td>\r\n                                      <td valign=\"top\">Производство мощности за текущий год), ГВт</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">29,40</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">43,05</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">57,30</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">113,10</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">248,24</p></td>\r\n                                    </tr>\r\n                                    <tr>\r\n                                      <td valign=\"top\"><p align=\"center\">7.</p></td>\r\n                                      <td valign=\"top\">Потери мощности, ГВт</td>\r\n                                      <td valign=\"top\"><p align=\"center\">65,45</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">87,40</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">97,56</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">151,77</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">220,14</p></td>\r\n                                    </tr>\r\n                                    <tr>\r\n                                      <td valign=\"top\"><p align=\"center\">8.</p></td>\r\n                                      <td valign=\"top\">Эффективность использования ресурсов, ЭИР</td>\r\n                                      <td valign=\"top\"><p align=\"center\">0,31</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">0,33</p></td>\r\n                                      <td valign=\"top\"><p align=\"center\">0,37</p></td>\r\n   